import React from 'react';

class TaskContainer extends React.Component {

  renderTasks = () => {
    const tasks = getTasks(this.props.tasks, this.props.selectedCategory)
  }

  getTasks = (tasks, category) => tasks.filter(task => task.category === category || category === 'All')

  render() {
    console.log(this.getTasks(this.props.tasks, this.props.selectedCategory));
    return <div className='tasks'>
      <h5>Tasks</h5>
      {this.renderTasks()}
    </div>
  }
}

export default TaskContainer;
