import React from 'react';

class TaskContainer extends React.Component {

  getTasks = category => this.props.tasks.filter(task => task.category === category || category === 'All')

  render() {
    debugger;
    return <div className='tasks'>
      <h5>Tasks</h5>
      {this.getTasks(this.props.selectedCategory)}
    </div>
  }
}

export default TaskContainer;
