import React from 'react';

class CategoryButton extends React.Component {

  state = {
    isSelected: false
  }

  render() {
    return <button className={this.state.isSelected ? 'selected' : ''} onClick={() => this.props.setSelectedCategory}>{this.props.category}</button>
  }
}

export default CategoryButton;
